mapper.select:
select
count(1)
from wj_user_info
where



    phoneNumber = #{token}



    email = #{token}

select count(1) from wj_user_info where passwd = MD5(#{passwd})
AND phoneNumber = #{token}
AND email = #{token}

mapper.update:
update wj_user_info
set passwd = MD5(#{passwd})
where phoneNumber = #{phoneNumber}
